import "./ExpenseItem.css"; 
/*React ensure we pass one parameter*/ 
/*Props-passing an object that holds all the property values which we get for our custom elements*/ 
/*We can name parameter data not even prop but for good coding practices name it props*/ 
function ExpenseItem(props) {

   const Month=prop.date.toLocaleString('en-US',{month:'long'});
   const Day=prop.date.toLocaleString('en-US',{day:'2-digit'});
   const Year=prop.date.getFullYear();

  return (
  
  <div className="expense-item">
      <div>
        <div>{Month}</div>
        <div>{Year}</div>
        <div>{Day}</div>
      </div>
      <div className="expense-item__description">
        <h2>{props.title}</h2>
        <div className='expense-item__price'>
          ${props.amount}
        </div>
      
      </div>
    </div>
  );
}

export default ExpenseItem;
